let Tasks=(s=>{return{init:()=>{s.ajax({url:ajax_object.ajax_url,type:"POST",dataType:"json",data:{action:"get_tasks"},success:function(t){if("error"===t.type)s(".todo-list__error").show();else if(0===t.data.length)s(".todo-list__empty").show();else{let e=s(".todo-list__tasks");t.data.forEach(t=>{e.append(`
    <li class="todo-list__tasks--item">
      <h3>${t.title}</h3>
      <p>${t.description}</p>
    </li>
    `)})}}})}}})(jQuery);jQuery(document).ready(function(){Tasks.init()});